<?php

namespace ResultadoBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Query\ResultSetMapping;

/**
 * ParticipanteRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ParticipanteRepository extends EntityRepository
{
    public function getParticipanteSospechosos()
    {
        return $this->getEntityManager()
                        ->createQuery(' SELECT p
                                        FROM ResultadoBundle:Participante p
                                        JOIN p.equipos e
                                        GROUP BY p.id
                                        HAVING COUNT(p.id) > 1
                                        ORDER BY p.apellido,p.nombre'
                                    )
                        ->getResult();
        ;
    }
    
    public function getParticipanteByDni($participante)
    {
        return $this->getEntityManager()
                        ->createQuery(' SELECT p
                                        FROM ResultadoBundle:Participante p 
                                        WHERE p.documentoNro = ?1 AND p.id <> ?2')
                        ->setParameter(1,$participante->getDocumentoNro())
                        ->setParameter(2,$participante->getId()?$participante->getId():0)
                        ->getoneornullresult();
        ;
    }
    
    public function getNombresDpl()
    {
        return $this->getEntityManager()
                        ->createQuery(' SELECT p
                                        FROM ResultadoBundle:Participante p
                                        WHERE  EXISTS (SELECT p1.id
                                                        FROM ResultadoBundle:Participante p1
                                                        WHERE UPPER(p1.nombre) = UPPER(p.nombre) AND UPPER(p.apellido) = UPPER(p1.apellido) AND p.id <> p1.id
                                                    )
                                        ORDER BY p.apellido,p.nombre'
                                    )
                        ->getResult();
        ;
    }
    
    public function getDniMay99M()
    {
        return $this->getEntityManager()
                        ->createQuery(' SELECT p
                                        FROM ResultadoBundle:Participante p
                                        WHERE p.documentoNro >99000000
                                        ORDER BY p.apellido,p.nombre'
                                    )
                        ->getResult();
        ;
    }
    
    public function getDniMen1M()
    {
        return $this->getEntityManager()
                        ->createQuery(' SELECT p
                                        FROM ResultadoBundle:Participante p
                                        WHERE p.documentoNro < 1000000
                                        ORDER BY p.apellido,p.nombre'
                                    )
                        ->getResult();
        ;        
    }
}
